import React, { useState, useRef, useEffect } from 'react';
import { Stage, Layer, Line, Rect } from 'react-konva';

const DrawingApp = () => {
  const [tool, setTool] = useState('pen');
  const [lines, setLines] = useState([]);
  const [color, setColor] = useState('#000000');
  const [lineWidth, setLineWidth] = useState(5);
  const stageRef = useRef(null);

  useEffect(() => {
    const handleMouseDown = () => {
        if (tool === 'pen' || tool === 'erase') {
          const stage = stageRef.current;
          const point = stage.getPointerPosition();
          const newLines = lines.concat([
            {
              tool,
              points: [{ x: point.x, y: point.y }],
              color,
              strokeWidth: lineWidth,
            },
          ]);
          setLines(newLines);
        }
      };
    const stage = stageRef.current;
  
    if (stage) {
      stage.addEventListener('mousedown', handleMouseDown);
    }
  
    return () => {
      if (stage) {
        stage.removeEventListener('mousedown', handleMouseDown);
      }
    };
  }, [tool]);
  

  const handleMouseDown = () => {
    setLines([]);
  };

  const handleMouseMove = (e) => {
    if (tool === 'pen' || tool === 'erase') {
      const stage = stageRef.current;
      const point = stage.getPointerPosition();
      const lastLine = lines[lines.length - 1];
  
      if (lastLine) {
        // Append the new point to the last line
        lastLine.points = lastLine.points.concat([point.x, point.y]);
        // Update state to trigger a re-render
        setLines([...lines.slice(0, -1), lastLine]);
      }
    }
  };

  const handleMouseUp = () => {
    // Save the drawn lines
  };

  const handleToolChange = (newTool) => {
    setTool(newTool);
    if (newTool === 'erase') {
      setColor('#ffffff');
    } else {
      setColor('#000000');
    }
  };

  const handleClear = () => {
    setLines([]);
  };

  return (
    <div>
      <div>
        <button onClick={() => handleToolChange('pen')}>Pen</button>
        <button onClick={() => handleToolChange('erase')}>Eraser</button>
        <button onClick={handleClear}>Clear</button>
      </div>
      <Stage
        width={window.innerWidth}
        height={window.innerHeight}
        onMouseDown={handleMouseDown}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
        ref={stageRef}
      >
        <Layer>
          {lines.map((line, i) => (
            <Line
              key={i}
              points={line.points}
              stroke={line.color}
              strokeWidth={line.strokeWidth}
              tension={0.5}
              lineCap="round"
              globalCompositeOperation={
                line.tool === 'erase' ? 'destination-out' : 'source-over'
              }
            />
          ))}
        </Layer>
      </Stage>
    </div>
  );
};

export default DrawingApp;
