import React, { useState } from 'react';
import Calendar from './Calendar'; // Adjust the path based on your project structure

const ExamCreation = () => {
  const [examName, setExamName] = useState('');
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);

  const handleStartDateClick = (day) => {
    const newStartDate = new Date(startDate || new Date());
    newStartDate.setDate(day);
    setStartDate(newStartDate);
  };

  const handleEndDateClick = (day) => {
    const newEndDate = new Date(endDate || new Date());
    newEndDate.setDate(day);
    setEndDate(newEndDate);
  };

  return (
    <div>
      <h1>Exam Details</h1>
      <div>
        <label>Exam Name:</label>
        <input
          type="text"
          value={examName}
          onChange={(e) => setExamName(e.target.value)}
        />
      </div>
      <div>
        <label>Start Date:</label>
        <Calendar selectedDate={startDate} onDateClick={handleStartDateClick} />
      </div>
      <div>
        <label>End Date:</label>
        <Calendar selectedDate={endDate} onDateClick={handleEndDateClick} />
      </div>
      <div>
        <p>Exam Name: {examName}</p>
        <p>Start Date: {startDate && startDate.toDateString()}</p>
        <p>End Date: {endDate && endDate.toDateString()}</p>
      </div>
    </div>
  );
};

export default ExamCreation;
